import '../index.css';
import api from '../utils/Api.js'

function Main(props) {

let userAvatar;
let userName;
let userDescription;

Promise.all([api.getProfile(), api.getCards()])
  .then(([userData, cards]) => {
    userName = userData.name;
    userDescription = userData.about;
    userAvatar = userData.avatar;
  })
  .catch((err) => {
    console.log(err);
  });



}

export default Main;
