import { useState } from 'react';
import { useHistory } from 'react-router-dom';

function Navbar({userEmail, buttonName}) {

  const [email, setEmail] = useState(userEmail);
  const [buttonText, setButtonText] = useState(buttonName);
  const history = useHistory();
  const signOut = () => {
    localStorage.removeItem('jwt');
    history.push("/signin");
  }

  const handleClick = () => {
    if (buttonName === 'Выйти') {
      signOut();
      setEmail('');
      setButtonText('Регистрация')
    }
    if (buttonName === 'Войти') {
      history.push("/signin");
      setButtonText('');
    }
    if (buttonName === 'Регистрация') {

      history.push("/signup");
    }
  }

  return (
    <nav className="menu">
      <span className="menu__user-email">{email}</span>
      <button className="menu__nav-button button" aria-label={buttonText} onClick={handleClick}>{buttonText}</button>
    </nav>
  );
}

export default Navbar;
