import { Component } from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import api from '../utils/Api';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import AddPlacePopup from './AddPlacePopup';
import ImagePopup from './ImagePopup';
import ConfirmDeletePopup from './ConfirmDeletePopup';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isEditProfilePopupOpen: false,
      isAddPlacePopupOpen: false,
      isEditAvatarPopupOpen: false,
      isConfirmPopupOpen: false,
      selectedCard: {},
      deleteCard: {},
      cards: [],
      currentUser: {}
    };
  }

  handleEditProfileClick = () => {
    this.setState({ isEditProfilePopupOpen: true });
  }

  handleAddPlaceClick = () => {
    this.setState({ isAddPlacePopupOpen: true });
  }

  handleEditAvatarClick = () => {
    this.setState({ isEditAvatarPopupOpen: true });
  }

  handleConfirmDeleteCardClick = () => {
    this.setState({ isConfirmPopupOpen: true });
  }

  handleEscClick = (event) => {
    if (event.key === 'Escape') {
      this.closeAllPopups();
    }
  }

  handleOutsideClick = (event) => {
    if (event.target.classList.contains('popup')) {
      this.closeAllPopups();
    }
  }

  handleUpdateUser = (userName, userDescription) => {
    api.setUserInfo(userName, userDescription)
      .then(userData => {
        this.setState({currentUser: userData});
        this.closeAllPopups();
      })
      .catch((err) => {
        console.log(err);
      });
  }

  handleUpdateAvatar = (userAvatar) => {
    api.setAvatar(userAvatar)
      .then(userData => {
        this.setState({currentUser: userData});
        this.closeAllPopups();
      })
      .catch((err) => {
        console.log(err);
      });
  }

  handleCardLike = (card) => {

    const isLiked = card.likes.some(like => like._id === this.state.currentUser._id);

    api.changeLikeCardStatus(card._id, isLiked)
      .then(getCard => {
        this.setState(
          {
            cards: this.state.cards.map(oldCard => oldCard._id === getCard._id ? getCard : oldCard)
          }
        );
      })
      .catch(err => {
        console.log(err);
      });
  }

  handleCardDelete = (deleteCard) => {
    console.log(this.state.deleteCard)
   /* api.deleteCard(deleteCard._id)
      .then(() => {
        this.setState(
          {
            cards: this.state.cards.filter(currentCard => currentCard._id !== deleteCard._id)
          }
        ); 
      })
      .catch((err) => {
        console.log(err);
      }); */
  }

  handleCardClick = (card) => {
    this.setState({ selectedCard: card });
  }

  handleAddPlace = (placeName, placeImage) => {
    api.setCard(placeName, placeImage)
    .then(newCard => {
      this.setState( {cards: [newCard, ...this.state.cards] })
      this.closeAllPopups();
    })
    .catch((err) => {
      console.log(err);
    });
  }

  componentDidMount() {
    api.getUserInfo()
      .then(userData => {
        this.setState( {currentUser: userData} )
      })
      .catch((err) => {
        console.log(err);
      });

      api.getCards()
      .then(getCardsArray => {
        this.setState(
          { cards: getCardsArray }
        );
      })
      .catch(err => {
        console.log(err);
      });

    document.addEventListener("keydown", this.handleEscClick);
    document.addEventListener("click", this.handleOutsideClick);
  }

  componentWillUnmount() {
    document.removeEventListener("keydown", this.handleEscClick);
    document.addEventListener("click", this.handleOutsideClick);
  }

  closeAllPopups = () => {
    this.setState(
      {
        isEditProfilePopupOpen: false,
        isAddPlacePopupOpen: false,
        isEditAvatarPopupOpen: false,
        isConfirmPopupOpen: false,
        selectedCard: {}
      }
    );
  }

  openConfirmDeletePopup = (card) => {
    this.closeAllPopups();
    this.setState({ isConfirmPopupOpen: true, deleteCard: card })
    console.log
  }

  render() {
    return (
      <div className="page">
        <div className="container">

          <CurrentUserContext.Provider value={this.state.currentUser}>

            <Header />

            <Main cards={this.state.cards} onEditProfile={this.handleEditProfileClick} onAddPlace={this.handleAddPlaceClick} onEditAvatar={this.handleEditAvatarClick} onCardLike={this.handleCardLike} onCardDelete={this.openConfirmDeletePopup} onCardClick={this.handleCardClick} />

            <Footer />

            <EditProfilePopup isOpen={this.state.isEditProfilePopupOpen} onUpdateUser={this.handleUpdateUser} onClose={this.closeAllPopups} />

            <EditAvatarPopup isOpen={this.state.isEditAvatarPopupOpen} onUpdateAvatar={this.handleUpdateAvatar} onClose={this.closeAllPopups} />

            <AddPlacePopup isOpen={this.state.isAddPlacePopupOpen} onAddPlace={this.handleAddPlace} onClose={this.closeAllPopups} />

            <ConfirmDeletePopup isOpen={this.state.isConfirmPopupOpen} onConfirmDeleteCard={this.handleCardDelete} onClose={this.closeAllPopups} />

            <ImagePopup card={this.state.selectedCard} onClose={this.closeAllPopups} />

          </CurrentUserContext.Provider>

        </div>
      </div>
    );
  } 
}

export default App;
