import React from 'react';
import { CurrentUserContext } from "../contexts/CurrentUserContext";

class PopupWithForm extends React.Component {

 /* const currentUser = React.useContext(CurrentUserContext);

  const [name, setName] = React.useState(currentUser.name);
  const [description, setDescription] = React.useState(currentUser.about);

  React.useEffect(() => {
    setName(currentUser.name);
    setDescription(currentUser.about);
  }, [currentUser]); */

  constructor(props) {
    super(props);
    this.state = { userName: null, userDescription: null };
  }

  static contextType = CurrentUserContext;

  componentDidMount() {
    this.setState( {userName: this.context.name, userDescription: this.context.about} )
  }

  handleNameChange = (e) => {
    this.setState( {userName: e.target.value} );
  }

  handleDescriptionChange = (e) => {
    this.setState( {userDescription: e.target.value} );
  }



  render() {

   const updateChildrenWithProps = (props) => {
      return this.props.children
    }

    const popupOpened = this.props.isOpen ? 'popup_opened' : '';

    return (
      <div className={`popup popup_role_${this.props.name} ${popupOpened}`}>
        <div className="popup__container">
          <h2 className="popup__heading">{this.props.title}</h2>
          <form className="form" name={this.props.name} onSubmit={this.props.onSubmit}>
            {updateChildrenWithProps()}
            <button className="form__submit button" type="submit" aria-label={this.props.buttonText}>{this.props.buttonText}</button>
          </form>
        </div>
        <button className="button popup__button-close" type="button" aria-label="Закрыть" onClick={this.props.onClose}></button>
      </div>
    );
  }
}

export default PopupWithForm;
