import React from 'react';
import '../index.css';
import Header from './Header.js';
import Main from './Main.js';
import Footer from './Footer.js';
import PopupWithForm from './PopupWithForm.js';

function App() {

  let isEditProfilePopupOpen = true;
  let isAddPlacePopupOpen = false;
  let isEditAvatarPopupOpen = false;

  function handleEditProfileClick() {
    isEditProfilePopupOpen = true;
  }

  function handleAddPlaceClick() {
    isAddPlacePopupOpen = true;
    document.querySelector('.popup_role_create').classList.add('popup_opened');
  }

  function handleEditAvatarClick() {
    isEditAvatarPopupOpen = true;
    document.querySelector('.popup_role_avatar-update').classList.add('popup_opened');
  }

  return (
    <div className="page">
      <div className="container">
        <Header />
        <Main onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onEditAvatar={handleEditAvatarClick}/>
        <Footer />
        <PopupWithForm name="edit" title="Редактировать профиль" buttonText="Сохранить" children="edit" isOpen={isEditProfilePopupOpen} />
        <PopupWithForm name="create" title="Новое место" buttonText="Создать" children="create" isOpen={isAddPlacePopupOpen} />
        <PopupWithForm name="avatar-update" title="Обновить аватар" buttonText="Сохранить" children="avatar-update" isOpen={isEditAvatarPopupOpen} />
        <PopupWithForm name="confirm" title="Вы уверены?" buttonText="Да"/>
      </div>
    </div>
  );
}

export default App;
