import React from 'react';
import '../index.css';
import Header from './Header.js';
import Main from './Main.js';
import Footer from './Footer.js';
import PopupWithForm from './PopupWithForm.js';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      isEditProfilePopupOpen: false,
      isAddPlacePopupOpen: false,
      isEditAvatarPopupOpen: false,
    }
  }


  handleEditProfileClick = () => {
    this.setState(isEditProfilePopupOpen = true);
    console.log('55')
  }

  handleAddPlaceClick = () => {
    this.setState({isEditProfilePopupOpen: true});
    //document.querySelector('.popup_role_create').classList.add('popup_opened');
  }

  handleEditAvatarClick = () => {
    this.setState({isEditProfilePopupOpen: true});
   // document.querySelector('.popup_role_avatar-update').classList.add('popup_opened');
  }

  render() {

    return (
      <div className="page">
        <div className="container">
          <Header />
          <Main onEditProfile={this.handleEditProfileClick} onAddPlace={this.handleAddPlaceClick} onEditAvatar={this.handleEditAvatarClick}/>
          <Footer />
          <PopupWithForm name="edit" title="Редактировать профиль" buttonText="Сохранить" children="edit" isOpen={this.isEditProfilePopupOpen} />
          <PopupWithForm name="create" title="Новое место" buttonText="Создать" children="create" isOpen={this.isAddPlacePopupOpen} />
          <PopupWithForm name="avatar-update" title="Обновить аватар" buttonText="Сохранить" children="avatar-update" isOpen={this.isEditAvatarPopupOpen} />
          <PopupWithForm name="confirm" title="Вы уверены?" buttonText="Да"/>
        </div>
      </div>
    );
  }

}

export default App;
