import React from 'react';
import PopupWithForm from './PopupWithForm';
import FieldsetPopupEdit from './FieldsetPopupEdit';
import { CurrentUserContext } from "../contexts/CurrentUserContext";

class EditProfilePopup extends React.Component {
  constructor(props) {
    super(props);
    this.state = { userName: '', userDescription: '' };
  }

  static contextType = CurrentUserContext;

  componentDidMount() {
    this.setState( {userName: this.context.name, userDescription: this.context.about} )
    console.log(this.context.name)
  }

  handleNameChange = (e) => {
    this.setState( {userName: e.target.value} );
  }

  handleDescriptionChange = (e) => {
    this.setState( {userDescription: e.target.value} );
  }

  handleSubmit = (event) => {
    event.preventDefault();
    this.props.onUpdateUser({

    })
  }

  render() {
    console.log(this.context.name)
    return (
      <PopupWithForm name="edit" title="Редактировать профиль" buttonText="Сохранить" isOpen={this.props.isOpen} onClose={this.props.onClose}>
        <fieldset className="form__fieldset">
          <div className="form__field">
            <input id="user-name-input" className="form__input form__input_role_user-name" type="text" name="user-name" value={this.state.name || ''} onChange={this.handleNameChange} placeholder="Ваше имя" required />
            <span className="form__input-error user-name-input-error"></span>
          </div>
          <div className="form__field">
            <input id="user-info-input" className="form__input form__input_role_user-info" type="text" name="user-info" value={this.state.description || ''} onChange={this.handleDescriptionChange} placeholder="Ваша профессия" required />
            <span className="form__input-error user-info-input-error"></span>
          </div>
        </fieldset>
      </PopupWithForm>
    );  
  }
}

export default EditProfilePopup;
