import React from 'react';
import '../index.css';

class PopupWithForm extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <>
        <div className={`popup popup_role_${this.props.name}`}>  {/*confirm, avatar-update, create, edit*/}
          <div className="popup__container">
            <h2 className="popup__heading">{this.props.title}</h2>
            <form className="form" name={this.props.name}>
              {this.props.children}
              <button className="form__submit button" type="submit" aria-label={this.props.buttonText}>{this.props.buttonText}</button> {/*Нажать для подтверждения/Да, Сохранить/Сохранить/Сохранение, Создать/Создать/Сохранение, Сохранить/Сохранить/Сохранение*/}
            </form>
          </div>
          <button className="button popup__button-close" type="button" aria-label="Закрыть"></button>
        </div>
      </>
    );
  }


}

export default PopupWithForm;
