import React from 'react';
import '../index.css';

function PopupWithForm(props) {
  return (
    <>
      <div className={`popup popup_role_${props.name}`}>  {/*confirm, avatar-update, create, edit*/}
        <div className="popup__container">
          <h2 className="popup__heading">{props.title}</h2>
          <form className="form" name={props.name}>
            {props.children}
            <button className="form__submit button" type="submit" aria-label={props.buttonText}>{props.buttonText}</button> {/*Нажать для подтверждения/Да, Сохранить/Сохранить/Сохранение, Создать/Создать/Сохранение, Сохранить/Сохранить/Сохранение*/}
          </form>
        </div>
        <button className="button popup__button-close" type="button" aria-label="Закрыть"></button>
      </div>
    </>
  );

}

export default PopupWithForm;
