import React from 'react';
import '../index.css';
import api from '../utils/Api.js'

class Main extends React.Component {
  constructor(props) {
    super(props);
    //this.state = { this.userName, this.userDescription, userAvatar };
  }  

  componentDidMount() {
    Promise.all([api.getProfile(), api.getCards()])
      .then(([userData, cards]) => {
        this.userName = userData.name;
        this.userDescription = userData.about;
        this.userAvatar = userData.avatar;
        this.setState({
          //userName: userData.name,
          //userDescription: userData.about,
          //userAvatar: userData.avatar
        })
      })
      .catch((err) => {
        console.log(err);
      });
  }

  render() { 
    return (
      <div>
        <section className="profile">
          <img className="profile__avatar" src={this.userAvatar} alt="Аватар"/>
          <div className="profile__edit-icon" onClick={this.props.onEditAvatar}></div>    
          <div className="profile__inner-container">
            <h1 className="profile__user-name">{this.userName}</h1>
            <button className="button profile__button-edit" type="button" aria-label="Редактировать" onClick={this.props.onEditProfile}></button>
          </div>
          <p className="profile__user-info">{this.userDescription}</p>
          <button className="button profile__button-add" type="button" aria-label="Добавить" onClick={this.props.onAddPlace}></button> 
        </section>

        <section className="card-repository">
          <ul className="cards">

          </ul>
        </section>
      </div>
    );
  }
}

export default Main;
