import { useContext } from 'react';
import { useHistory } from 'react-router-dom';
import { MenuHeadingContext } from '../contexts/CurrentUserContext';

function Navbar({userEmail, buttonName}) {

  const history = useHistory();
  const signOut = () => {
    localStorage.removeItem('jwt');
    history.push("/signin");
  }
  const menuHeading = useContext(MenuHeadingContext);

  const handleClick = () => {
    if (buttonName === 'Выйти') {
      signOut();
    }
    if (buttonName === 'Войти') {

      history.push("/signin");
    }
    if (buttonName === 'Регистрация') {
      history.push("/signup")
    }
  }

  return (
    <nav className="menu">
      <span className="menu__user-email">{userEmail}</span>
      <button className="menu__nav-button button" aria-label={buttonName} onClick={handleClick}>{buttonName}</button>
    </nav>
  );
}

export default Navbar;
