import { Route, Switch, Link, useHistory } from 'react-router-dom';
import { useEffect } from 'react';

function Navbar({userEmail, resetLogedIn}) {

  const history = useHistory();
  const signOut = () => {
    localStorage.removeItem('jwt');
    resetLogedIn();
    history.push("/signin");
  }

  useEffect(() => {
    setUserEmail(userEmail);
  }, [logedIn]);

  return (
    <nav className="menu">
      <Switch>
        <Route path="/main">
          <p className="menu__user-email">{userEmail}</p>
          <Link to="signin" className="entry-screen__login-link" onClick={signOut}>Выйти</Link>
        </Route>
        <Route path="/signin">
          <Link to="signup" className="entry-screen__login-link">Регистрация</Link>
        </Route>
        <Route path="/signup">
          <Link to="signin" className="entry-screen__login-link">Войти</Link>
        </Route>
      </Switch>
    </nav>
  );
}

export default Navbar;
